#Combien d'handle contiennent un numéro ?
def countNumber
  counter = 0
  @data_set.map {|x|
    if  x.scan(/[0123456789]/).length > 0
    counter += 1
  end}
puts "2. Le tableau contient #{counter} éléments qui ont des chiffres. "
end
countNumber

#Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite ?
def aude
  counter = 0
  @data_set.map {|x|
    x = x.downcase
     if x.include?('aude')
      counter += 1
  end}
puts "3. Le tableau contient #{counter} éléments qui contiennent 'aude'. "
end
aude

#Combien commencent par une majuscule ?
def upperKey(c)
 c >= 'A' && c <= 'Z'
end

def majStart
  counter1 = 0
  @data_set.map {|x|
    if upperKey(x[1])
     counter1 += 1
  end}
puts "4. Le tableau contient #{counter1} élements qui commencent par une majuscule."
end
majStart

#Combien contiennent une majuscule ?
def majuscule
counter2 = 0
  @data_set.map {|x|
    if  x.scan(/[ABCDEFGHIJKLMNOPQRSTUVWXYZ]/).length > 0
      counter2 += 1
  end}
      puts "5. Le tableau contient #{counter2} éléments avec au moins une majuscule."
end
majuscule

#Combien de _ dans tous les pseudos confondus ?
def count_
  counter3 = 0
  @data_set.map { |x|
    if x.scan(/[_]/).length > 0
      counter3 += 1
    end}
puts "6. Le tableau contient #{counter3} éléments avec un '_'."
end
count_

#Trie la liste de handle par ordre alphabétique
def alphabet
  arrSort = @data_set.sort_by {|x| x.downcase }
puts "7. Voici le tableau réorganisé par ordre alphabétique : \n #{arrSort}"
end
